mapper.update:
UPDATE account
username = #{username}, password = #{password}, email = #{email}, long_term_usage_count = #{longTermUsageCount}, limited_term_usage_count = #{limitedTermUsageCount}, limited_term_expire_days = #{limitedTermExpireDays}, call_successful = #{callSuccessful}, last_modify = #{lastModify}
WHERE `id` = #{id}
